<?php//Configuración$host 	= "localhost";$db	= "tetm";$user	= "root";$pass	= "";//Conexióntry{$conn = new PDO("mysql:host=$host;dbname=$db",$user,$pass);}catch(PDOException $pe){	die('Error de conexion, Mensaje: ' -$pe->getMessage());}//Consulta  $sql = "SELECT ip.tipo_jugador, j.nombre   FROM informacion_personal ip JOIN jugadores j ON (j.id_jugador=ip.id_jugador)   WHERE ip.tipo_jugador= 'Nacional'   group by ip.tipo_jugador, j.nombre  order by j.id_jugador";//Ejecucion$q	 = $conn->query($sql);//Si es null, hubo un errorif(!$q) {  die("Error al ejecutar una consulta, Mensaje: ". $conn->errorInfo());}?><!DOCTYPE html>html><head>	<title>Ttm</title>	<link rel="stylesheet" type="text/css" href="css/estilotm.css"/> 	<script type="text/javascript" src="js/fecha.js"></script></head><body>	<header>		 <script type="text/javascript">  			document.write(retornarFecha());         </script>	</header>	<nav>			<ul id="contenedorMenu">				<li><a href="home.php">Inicio</a></li>				<li><a href="historia.php">Historia</a></li>				<li><a href="reglamento.php">Reglamento</a></li>				<li><a>Materiales</a>			                    <ul>						<li><a href="gomas.php">Gomas</a></li>						<li><a href="maderas.php">Maderas</a></li>					</ul>				</li>				<li><a>Jugadores</a>					<ul>						<li><a href="jugadores_mundiales.php">Mundiales</a></li>						<li><a href="jugadores_regionales.php">Nacionales</a></li>					</ul>					</li>			</ul>	</nav>	<section class = "informacion">			<h1> JUGADORES NACIONALES </h1>  			 <?php					  $q = $conn->query($sql);					  while($r = $q->fetch()){					?>									<p> + <?= $r["nombre"]?></p>								<?php							}						?>		</section>	<footer>		<p>			&copy 2014 Argentina-Tandil cp7000 -All rights reserved by John-		</p></footer></body></html>